mapper.select:
        SELECT


--         级联表的字段
        ,cheliang.shangjia_id as cheliangShangjiaId
        ,cheliang.cheliang_name as cheliangName
        ,cheliang.cheliang_photo as cheliangPhoto
        ,cheliang.cheliang_types as cheliangTypes
        ,cheliang.cheliang_new_money as cheliangNewMoney
        ,cheliang.cheliang_clicknum as cheliangClicknum
        ,cheliang.zan_number as zanNumber
        ,cheliang.cai_number as caiNumber
        ,cheliang.cheliang_content as cheliangContent
        ,cheliang.shangxia_types as shangxiaTypes
        ,cheliang.cheliang_delete as cheliangDelete
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail

        FROM cheliang_liuyan  a
        left JOIN cheliang cheliang ON a.cheliang_id = cheliang.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and (
                    a.cheliang_id = #{params.cheliangId}
                )


                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and a.cheliang_liuyan_text like CONCAT('%',#{params.cheliangLiuyanText},'%')


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


                and a.reply_text like CONCAT('%',#{params.replyText},'%')


                  and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


                  and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 




                and a.cheliang_id IS NOT NULL


                and cheliang.shangjia_id = #{params.shangjiaId}


                and cheliang.cheliang_name like CONCAT('%',#{params.cheliangName},'%')


                and cheliang.cheliang_types = #{params.cheliangTypes}



                  and cheliang.cheliang_new_money >= #{params.cheliangNewMoneyStart}   


                  and cheliang.cheliang_new_money <= #{params.cheliangNewMoneyEnd}   


                  and cheliang.cheliang_clicknum >= #{params.cheliangClicknumStart}   


                  and cheliang.cheliang_clicknum <= #{params.cheliangClicknumEnd}   


                and cheliang.cheliang_clicknum = #{params.cheliangClicknum}


                  and cheliang.zan_number >= #{params.zanNumberStart}   


                  and cheliang.zan_number <= #{params.zanNumberEnd}   


                and cheliang.zan_number = #{params.zanNumber}


                  and cheliang.cai_number >= #{params.caiNumberStart}   


                  and cheliang.cai_number <= #{params.caiNumberEnd}   


                and cheliang.cai_number = #{params.caiNumber}


                and cheliang.cheliang_content like CONCAT('%',#{params.cheliangContent},'%')


                and cheliang.shangxia_types = #{params.shangxiaTypes}



                  and cheliang.cheliang_delete >= #{params.cheliangDeleteStart}   


                  and cheliang.cheliang_delete <= #{params.cheliangDeleteEnd}   


                and cheliang.cheliang_delete = #{params.cheliangDelete}



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')



        order by a.${params.orderBy} desc
